import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';




//import fullcalendar from 'fullcalendar/dist/fullcalendar.min.css';
//import fullcalendarscheduler from 'fullcalendar-scheduler/dist/scheduler.min.css'
import fullcalendarlocale from 'fullcalendar-scheduler/node_modules/fullcalendar/dist/locale/es.js';

import $ from 'jquery';



class Calendar extends Component {
  componentDidMount(){
    $(document).ready(function() {
    var language = 'es';

      $('#calendar').fullCalendar({
        //funcio para seleccionar un dia
        dayClick: function() {
          alert('a day has been clicked!');
        },

        //tema del calendario
        themeSystem: 'standard',
        //vista por defecto
        defaultView: 'month',
        //cabecera del calendario
        header: {
          left: 'prev,next, today',
          center: 'title',
          right: 'month,agendaWeek,agendaDay,listMonth'
        },
        buttonText: {
          month: 'Mes',
          agendaWeek: 'Semana',
          agendaDay: 'Día',
          listMonth: 'Listado',
          today: 'Hoy',
          week: 'S'
        },
        monthNames: ['Enero','Febrero','Marzo','Abril','Mayo','Junio','Julio','Agosto','Septiembre','Octubre','Noviembre','Diciembre'],
        monthNamesShort: ['Ene','Feb','Mar','Abr','May','Jun','Jul','Ago','Sep','Oct','Nov','Dic'],
        dayNames: ['Domingo','Lunes','Martes','Miércoles','Jueves','Viernes','Sábado'],
        dayNamesShort: ['Dom','Lun','Mar','Mié','Jue','Vie','Sáb'],
        locale: language,
        //altura del contenido, del calendario y botones
        contentHeight: 500,
        // numeros de la semana del año a la izquierda
        weekNumbers: true,
        eventLimit: true,
          schedulerLicenseKey: 'CC-Attribution-NonCommercial-NoDerivatives'
      });
    });
  }


  render() {

    return(
      <div className="calendar">
        <div id='calendar'>
        </div>
      </div>
    )
  }
}

export default withAuthConsumer(Calendar);
