import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import classroomService from '../../services/classroom-service';
import { Button, Modal, FormControl, InputGroup } from 'react-bootstrap';

const validations = { email: (value) => {
  let message;
  if(!value){
    message = 'Email is required'
  } 
  else if(!EMAIL_PATTERN.test(value)){
    message = 'Invalid email pattern'
  }
  return message;
}}

class AddStudent extends Component {
  state = {
    students: [],
    errors: {
      email: ''
    }
  }

  handleChange = (event) => {

    const { name, value } = event.target;

    this.setState({ 
      classroom: {
        ...this.state.classroom,
        [name]: value
      },
      errors: {
        ...this.state.errors,
        [name]: validations[name] && validations[name](value)
      }
    })
  }

  handleBLur = (event) => {
    const { name } = event.target;

    this.setState({
      ...this.state.touch,
      [name]: true
    })
  }

  createClassroom = (event) => {
    event.preventDefault();

    const classroomData = {
      ...this.state.classroom,
      teachers: this.props.user.id
    }
    classroomService.createClassroom(classroomData)
    .then(this.props.fetchClassrooms)
    .then(this.setState({ show: false }))
  }


  handleClose = () => {
    this.setState({ show: false });
  }

  handleShow = () => {
    this.setState({ show: true });
  }


  render() {
    const { errors } = this.setState

    return(
      <>
        <Button variant="outline-info ml-3" onClick={this.handleShow}>
          +
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Add News Students</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <InputGroup className="mb-3">
              <FormControl
                placeholder="Student email..."
                name="name" 
                value={this.state.beer.name}
                onChange={this.handleChange}  
                error={this.state.errors.name}
                onBlur={this.handleBlur} 
                onTouch={this.state.touch.name}
              />
              <div className="invalid-feedback">{errors.email}</div>
              <InputGroup.Append>
                <Button variant="outline-secondary">+ Add</Button>
              </InputGroup.Append>
            </InputGroup>
            <ul>
              <li> Nombre y mail del alumno</li>
            </ul>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={this.handleClose}>
              Save students
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    )
  }
}

export default withAuthConsumer(AddStudent);
