import React, { Component } from 'react';
import { Collapse, Button, ButtonGroup, Nav } from 'react-bootstrap';
import Chronometer from './Chronometer';
import Countdown from './Countdown';
import NavTimer from './NavTimer';

class Timer extends Component{

  state ={
    show: false,
    chronometer: false,
    countdown: false,
    input
  }

  myHandleChange = (e) =>{
    const { name, value } = e.target;
    this.setState({ 
      event: {
        ...this.state,
        [name] : value
      }
    })
  }

  render(){
    const { chronometer, countdown } = this.state;

    return(

      <div className="container-timer">
        <ButtonGroup className="btn-group-timer">
        <Button
          onClick={() => this.setState({ countdown: !countdown, chronometer: false })}
          aria-controls="collapse-countdown"
          aria-expanded={countdown}
          variant="outline-secondary"
        >
          Countdown
        </Button>
        <Button
          onClick={() => this.setState({ chronometer: !chronometer, countdown: false })}
          aria-controls="collapse-chronometer"
          aria-expanded={chronometer}
          variant="outline-secondary"
        >
          Chronometer
        </Button>
      </ButtonGroup>
      <Collapse in={this.state.countdown}>
        <div id="collapse-countdown" className="collapse-c">
          <Countdown {...this.props}/>
          <input type="number" min="1" value={this.state.input} name="input" onChange={this.myHandleChange}/>
        </div>

      </Collapse>
      <Collapse in={this.state.chronometer}>
        <div id="collapse-chronometer" className="collapse-c">
          <Chronometer />
        </div>
      </Collapse>
    </div>
    )
  }
}

export default Timer;
