import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import { Modal, Button } from 'react-bootstrap';
import calendarService from '../../services/calendar-service';

const validations = { title: (value) => value.length > 0 && value.length < 30}
class CreateEvent extends Component {

  state = {
    event: {
      title: '',
      classroom: this.props.classroom.id,
      start: '',
      end
    },
    errors: {
      title: true
    },
    touch: {},
    show: false
  }

  handleChange = (event) => {

    const { name, value } = event.target;

    this.setState({ 
      column: {
        ...this.state.column,
        [name]: value
      },
      errors: {
        ...this.state.errors,
        [name]: validations[name] && validations[name](value)
      }
    })
  }

  handleBLur = (event) => {
    const { name } = event.target;

    this.setState({
      ...this.state.touch,
      [name]: true
    })
  }

  createColumn = (event) => {
    event.preventDefault();

    const column = {
      ...this.state.column
    }
    calendarService.createEvent(column)
    .then(this.props.fetchColumns)
    .then(this.setState({ show: false }))
  }

  handleClose = () => {this.setState({ show: false });}

  handleShow = () => {this.setState({ show: true });}

  render() {
    const { event, touch, errors, show } = this.state;


    return(
      <>
          <Button variant="outline-info" onClick={this.handleShow}>+ Create Event</Button>

          <Modal show={show} onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>New event</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <form onSubmit={this.createColumn}>
                <div className="form-group">
                  <label htmlFor="titleEvent">Title</label>
                  <input type="text" 
                    className={`form-control form-control-sm ${touch.title ? (errors.title ? 'is-invalid' : 'is-valid') : ''}`} 
                    id="titleEvent" 
                    placeholder="Ex. Exams for training..."
                    value={event.title} 
                    onChange={this.handleChange}
                    onBlur={this.handleBlur}
                    name="title"
                    autoComplete="off"
                    />
                </div>
              </form>
            </Modal.Body>
              <Modal.Footer>
                <Button variant="info" onClick={this.createColumn}>Create</Button>
              </Modal.Footer>
          </Modal>
      </>
    )
  }
}

export default withAuthConsumer(CreateEvent) ;
