import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import classroomService from '../../services/classroom-service';
import { Form, Button } from 'react-bootstrap';
import  Select from 'react-select';
import { createBrotliDecompress } from 'zlib';

class EditClassroom extends Component{

  state = {
    disabled: true,
    classroom: {
      students: [],
      title: this.props.classroom.title,
      accountPay: this.props.classroom.accountPay
    },
    studentSelected:[]

  }

  handleChange = (event) => {

    const { value } = event.target;

    this.setState({ 
      studentSelected: value,
      disabled: false
    })
  }

  handleSubmit = (event) => {
    event.preventDefault();

    classroomService.editClassroom(this.state.classroom)

  }

  componentDidMount(){
    const opt = this.props.classroom.students
      .map(student => { return { value: student.id, label: student.name } })
    this.setState({
      classroom:{ students: opt}
    })
  }

  handleStudentSelect = (studentSelected) => {
    console.log("lo")
    this.setState({ studentSelected });
  }

  render(){
    const { disabled, classroom, studentSelected } = this.state
    const value = studentSelected && studentSelected.value;
    return(
      <Select
        className="mt-2"
        value={value}
        onChange={this.handleStudentSelect}
        options={classroom.students}
        isMulti
      />

    )
  }
}

export default withAuthConsumer(EditClassroom)
