import http from './base-http-service';

const listColumns = () => http.get('/board')
  .then(response => response.data);

const createColumn = (column) => http.post('/board', column);

const deleteColumn = (id) => http.delete(`/board/${id}`);

const createCard = (card) => {
  const data = new FormData();
  Object.keys(card).forEach(key => {
    data.append(key, card[key]);
  })

  return http.post('/board/cards', data);
}

const deleteCard = (id) => http.delete(`/cards/${id}`);

export default {
  listColumns,
  createColumn,
  deleteColumn,
  createCard,
  deleteCard
};
