import React, { Component } from 'react';
import { Link, Redirect } from 'react-router-dom';
import logoCoolSchool from '../../logo-coolSchool.svg'

const EMAIL_PATTERN = /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;

const validators = {
  email: (value) => {
    let message;
    if(!value){
      message = 'Email is required'
    } 
    else if(!EMAIL_PATTERN.test(value)){
      message = 'Invalid email patern'
    }
    return message;
  },
  password: (value) => {
    let message;
    if(!value){
      message = 'Password is required'
    }
    return message;
  }
}

class Login extends Component {

  state = {
    user:{
      email: '',
      password: ''
    },
    errors: {
      email: validators.email(),
      password: validators.password()
    },
    touch: {},
    isAuthenticated: false
  }

  handleChange = () => {

  }

  handleBlur = () => {

  }

  handleSubmit = () => {

  }

  isValid = () => {

  }


  render(){
    const { user, errors, touch, isAuthenticated } = this.state
    return(
      <div className="login shadow-sm">
        <div className="login-body">
          <img className="w-75 mx-auto my-3" src={logoCoolSchool} alt=""/>
          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label htmlFor="emailLogin">Email</label>
              <input type="email" 
                className="form-control" 
                id="emailLogin" 
                placeholder="Email"
                onChange={this.handleChange}
                value={user.email}
                onBlur={this.handleBlur}
                />
            </div>
            <div className="form-group">
              <label htmlFor="passwordLogin">Contraseña</label>
              <input type="password" 
                  className="form-control" id="passwordLogin" placeholder="Contraseña"/>
            </div>
            <button type="submit" className="btn btn-info w-100">Entrar</button>
          </form>
          <p className="mx-auto mt-4">Si no tienes cuenta <a href=""> REGISTRATE </a>ahora</p>
        </div>
      </div>
    )
  }
}

export default Login;
