import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import classroomService from '../../services/classroom-service';
import { Form, Button } from 'react-bootstrap';

class EditClassroom extends Component{

  state = {
    disabled: true,
    classroom: {
      students: [],
      title: '',

    }

  }

  handleChange = (event) => {

    const { value } = event.target;

    this.setState({ 
      studentSelected: value,
      disabled: false
    })
  }

  handleSubmit = (event) => {
    event.preventDefault();

    classroomService.editClassroom(this.state.classroom)

  }

  render(){
    const { disabled } = this.state
    return(
      <Form.Group className="form-detail">
          <Form.Label className="text-secondary">Delete student a student of this classroom</Form.Label>
          <div className="form-detail-items">
            <Form.Control as="select" onChange={this.handleChange} className="select-detail">
              <option>Select one ...</option>
              {this.props.classroom.students.map(student => 
                <option key={student.id} value={student.id} name="student">{student.name} {student.surname}</option>
                )}

            </Form.Control>
            {/* <Button onClick={this.handleSubmit} variant="outline-danger ml-2" disabled={disabled}><i className="far fa-trash-alt"></i></Button> */}
          </div>

        </Form.Group>

    )
  }
}

export default withAuthConsumer(EditClassroom)
