import React, { Component } from 'react';
import { Link, Redirect} from 'react-router-dom';
import logoCoolSchool from '../../logo-coolSchool.svg';
import authService from '../../services/auth-service';

const EMAIL_PATTERN = /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;


const validations = {
  email: (value) => {
    let message;
    if(!value){
      message = 'Email is required'
    }
    else if(!EMAIL_PATTERN.test(value)){
      message = 'Invalid email pattern '
    }
    return message;
  },
  password: (value) => {
    let message;
    if(!value){
      message = 'Password is required'
    }
    return message;
  },
  name: (value) => {
    let message;
    if(!value){
      message = 'Name is required'
    }
    return message;
  },
  role: (value) => {
    let message;
    if(!value){
      message = 'Role is required'
    }
    return message;
  }
}
class Register extends Component {
  state = {
    user: {
      email: '',
      password: '',
      name: '',
      role: ''
    },
    errors: {
      email: validations.email(),
      password: validations.password(),
      name: validations.name(),
      role: validations.role()
    },
    touch: {},
    isRegistered: false
  }

  handleChange = () => {

  }

  handleBlur = () => {

  }

  handleSubmit = () => {

  }

  isValid = () => {

  }

  render(){

    return(
      <div className="login shadow-sm">
        <div className="login-body">
            <img className="w-75 mx-auto my-3" src="./logo coolScool.svg" alt=""/>
            <form>
              <div className="form-group">
                <label htmlFor="emailRegister">Email</label>
                <input type="email" className="form-control" id="emailRegister" aria-describedby="emailHelp" placeholder="Email"/>
              </div>
              <div className="form-group">
                <label htmlFor="passwordRegister">Contraseña</label>
                <input type="password" className="form-control" id="passwordRegister" placeholder="Contraseña"/>
              </div>
              <div className="form-group">
                <label htmlFor="nameRegis">Nombre y Apellidos</label>
                <input type="text" className="form-control" id="nameRegis" placeholder="Nombre completo"/>
              </div>
              <div className="form-check form-check-inline">
                <input className="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio1" value="option1"/>
                <label className="form-check-label" htmlFor="inlineRadio1">Profesor</label>
                </div>
              <div className="form-check form-check-inline">
                <input className="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio2" value="option2"/>
                <label className="form-check-label" htmlFor="inlineRadio2">Alumno</label>
              </div>
                <button type="submit" className="btn btn-info w-100 mt-3">Entrar</button>
            </form>
            <p className="mx-auto mt-4">Si ya tienes cuenta <a href=""> ENTRA </a>en tu espacio</p>        
        </div>
      </div>
    )
  }
}

export default Register;
