import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import calendarService from '../../services/calendar-service';

import FullCalendar from 'fullcalendar-reactwrapper';
import corePlugin from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import listPlugin from '@fullcalendar/list';


import CreateEvent from './CreateEvent';
import EditEvent from './EditEvent';
import { Modal } from 'react-bootstrap';

class Calendar extends Component {

  state = {
    events:[]	
    }

    fetchEvents = () => {
      calendarService.listEvent(this.props.classroom.id)
      .then(events => this.setState({ events }))
    }

    componentDidMount(){
      this.fetchEvents();
    }

    opHandler = (event) => {
      console.log("eooo", event)

    }


  render() {
    const plugins = [ dayGridPlugin, timeGridPlugin, listPlugin, corePlugin ]
    return(
      <div className="mt-4 p-1">
        <FullCalendar
        id = "calendar"
        header = {{
            left: 'prev,next today myCustomButton',
            center: 'title',
            right: 'month,basicWeek,basicDay'
        }}


        eventClick={this.opHandler}
        navLinks= {true} // can click day/week names to navigate views
        editable= {true}
        eventLimit= {true} // allow "more" link when too many events
        events = {this.state.events}	
        />
        <div className="d-flex justify-content-center mt-3">
          <CreateEvent fetchEvents={this.fetchEvents}/>
        </div>
        <Modal show={show} onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>New event</Modal.Title>
            </Modal.Header>
            <Modal.Body>

            </Modal.Body>
              <Modal.Footer>
                <Butto variant="info" onClick={this.createEvent}>Edit Event</Butto>
                <Button variant="danger" onClick={this.createEvent}>Delete Event</Button>
              </Modal.Footer>
          </Modal>
      </div>
    )
  }
}

export default withAuthConsumer(Calendar);
