import React, {Component} from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import { Modal, Button, Collapse } from 'react-bootstrap';
import boardService from '../../services/board-service';
import { NavLink } from 'react-router-dom';


// const queryString = require('query-string');

const validators = {
    title:     v => v.length > 0,
    description:    v => v.length > 0,
    position: v => v.length > 0,
    column: v => v.length > 0
  }
class CreateCard extends Component {

    state = {
        card: {
          title: '',
          description: '',
          column: '',
          attach:'',
          filePreview:''
        },
        errors: {
          title: true,
          description: true,
          position: true
        },
        touch: {},
        onSubmit: false,
        show: false,
        open: false
      }

      handleChange = (e) => {
        const { name, value, files } = e.target;

        const isValid = validators[name] === undefined || validators[name](value);

        this.setState({
          card: {
            ...this.state.card,
            [name]: (files && files[0]) ? files[0] : value
          },
          errors: {
            ...this.state.errors,
            [name]: !isValid
          } 

        })
      }

      handleBlur = (e) => {
        this.setState({
          touch: {
            ...this.state.touch,
            [e.target.name]: true
          }
        })
      }



    postCard = (e) => {
      e.preventDefault();

      const cardData = {
        ...this.state.card
      }

      boardService.createCard(cardData)
        .then(() => this.setState({ onSubmit: true }))
    }

    handleClose = () => {this.setState({ show: false });}

    handleShow = () => {this.setState({ show: true });}

render(){

    const isError = Object.values(this.state.errors).some(error => error);
    const { card, errors, touch, show, open } = this.state;

    return (
      <>
        <Button variant="outline-secondary" className="fas fa-plus m-2 rounded-pill" onClick={this.handleShow}></Button>

        <Modal show={show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Create a new card</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <form onSubmit={this.createColumn}>
              <div className="form-group">
                <label htmlFor="titleCard">Title</label>
                <input type="text" 
                  className={`form-control form-control-sm ${touch.title ? (errors.title ? 'is-invalid' : 'is-valid') : ''}`} 
                  id="titleCard" 
                  value={card.title} 
                  onChange={this.handleChange}
                  onBlur={this.handleBlur}
                  name="title"
                  autoComplete="off"
                  />
              </div>

              <div className="form-group">
                <label htmlFor="descriptionCard">Description</label>
                <textarea type="text" 
                  className={`form-control form-control-sm ${touch.description ? (errors.description ? 'is-invalid' : 'is-valid') : ''}`} 
                  id="descriptionCard" 
                  value={card.description} 
                  onChange={this.handleChange}
                  onBlur={this.handleBlur}
                  name="description"
                  autoComplete="off"
                  rows="3"
                  />
              </div>

              {/* {this.state.card.attach && <img className="img-upload" src={URL.createObjectURL(this.state.card.attach)} alt="..."/>}

              <button type="button" className="add-img btn btn-outline-info">
                <input 
                    type="file" 
                    className="custom-file-input" 
                    name="attach" id="attach" 
                    onChange={this.handleChange}
                    onBlur={this.handleBlur}
                    lang="es"/> 
                    <i className="abc fas fa-image"></i>
              </button> */}
               <Button
                  onClick={() => this.setState({ open: !open })}
                  aria-controls="collapse-date"
                  aria-expanded={open}
                >
                  Date
                </Button>
                <Button
                  onClick={() => this.setState({ open: !open })}
                  aria-controls="collapse-date"
                  aria-expanded={open}
                >
                  click
                </Button>
                <Collapse in={this.state.open}>
                  <div id="collapse-date">
                  <div className="m-2">
                      <div className="mt-3">
                        <label htmlFor="recipient-name" className="col-form-label">Fecha de Inicio</label>
                        <input type="date" name="cumpleanios" step="1" min="2018-01-01" max="" value=""/>
                      </div>
                      <div className="mt-3">
                        <label htmlFor="recipient-name" className="col-form-label">Fecha de Fin</label>
                        <input className="ml-3" type="date" name="cumpleanios" step="1" min="2018-01-01" max="" value=""/>
                      </div>

                      <div className="custom-control custom-switch mt-2">
                        <input type="checkbox" className="custom-control-input" id="customSwitch1"/>
                        <label className="custom-control-label" htmlFor="customSwitch1">Incluir este evento en el calendario</label>
                      </div>
                    </div>
                  </div>
                </Collapse>
              {/* <nav>
                <div className="nav nav-tabs" id="nav-tab" role="tablist">
                  <NavLink className="nav-item nav-link active" id="nav-date-tab" data-toggle="tab" to="#nav-date" role="tab" aria-controls="nav-date" aria-selected="true">Fecha</NavLink>
                  <NavLink className="nav-item nav-link" id="nav-attach-tab" data-toggle="tab" to={"#nav-attach"} role="tab" aria-controls="nav-attach" aria-selected="false">Adjutar</NavLink>
                  <NavLink className="nav-item nav-link"  to="#nav-pay" >Pago</NavLink>
                </div>
              </nav>
                <div className="tab-content" id="nav-tabContent">
                  <div className="tab-pane fade show active" id="nav-date" role="tabpanel" aria-labelledby="nav-home-tab">
                    <div className="m-2">
                      <div className="mt-3">
                        <label htmlFor="recipient-name" className="col-form-label">Fecha de Inicio</label>
                        <input type="date" name="cumpleanios" step="1" min="2018-01-01" max="" value=""/>
                      </div>
                      <div className="mt-3">
                        <label htmlFor="recipient-name" className="col-form-label">Fecha de Fin</label>
                        <input className="ml-3" type="date" name="cumpleanios" step="1" min="2018-01-01" max="" value=""/>
                      </div>

                      <div className="custom-control custom-switch mt-2">
                        <input type="checkbox" className="custom-control-input" id="customSwitch1"/>
                        <label className="custom-control-label" htmlFor="customSwitch1">Incluir este evento en el calendario</label>
                      </div>
                    </div>
                  </div>

                  <div className="tab-pane fade" id="nav-attach" role="tabpanel" aria-labelledby="nav-attach-tab">
                    <div className="m-2">
                      <label htmlFor="recipient-name" className="col-form-label">Subir fotos o archivos</label>

                    </div>
                  </div>

                  <div className="tab-pane fade" id="nav-pay" >
                    <div className="m-2">
                      <label htmlFor="recipient-name" className="col-form-label">Numero cuenta:</label>
                      <input type="text" className="form-control" id="recipient-name"/>
                      <label htmlFor="recipient-name" className="col-form-label">Concepto del pago:</label>
                      <input type="text" className="form-control" id="recipient-name"/>
                      <label htmlFor="recipient-name" className="col-form-label">Importe:</label>
                      <input type="text" className="form-control w-50" id="recipient-name"/>
                    </div>
                  </div>
                </div> */}


            </form>
          </Modal.Body>
            <Modal.Footer>
              <Button variant="info" onClick={this.createColumn}>Create</Button>
            </Modal.Footer>
        </Modal>
      </>
      //   <div className="container col-4 mt-5">
      //   <form onSubmit={this.postCard}>
      //    
      //     {this.state.card.attach && <img className="img-upload" src={URL.createObjectURL(this.state.card.attach)} alt="..."/>}

      //       <button type="button" className="bot btn btn-outline-primary">
      //         <input 
      //             type="file" 
      //             className="custom-file-input" 
      //             name="attach" id="attach" 
      //             aria-describedby="attach"
      //             onChange={this.handleChange}
      //             onBlur={this.handleBlur}
      //             lang="es"/> <i className="abc fas fa-plus"></i>
      //       </button>

      //     <div className="control">
      //       <button className="btn btn-primary mt-3" disabled={isError}>
      //         <span>+ Add</span>
      //       </button>
      //     </div>
      //   </form>
      // </div>

    )
}}


export default withAuthConsumer(CreateCard) ;
