import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import classroomService from '../../services/classroom-service';
import { Form, Button } from 'react-bootstrap';
import  Select from 'react-select';
import { createBrotliDecompress } from 'zlib';

class EditClassroom extends Component{

  state = {
    disabled: true,
    classroom: {
      students: [],
      title: '',
      accountPay: 'this.props.classroom.accountPay'
    },
    studentSelected:[]

  }

  handleChange = (event) => {

    const { value } = event.target;

    this.setState({ 
      studentSelected: value,
      disabled: false
    })
  }

  handleSubmit = (event) => {
    event.preventDefault();

    classroomService.editClassroom(this.state.classroom)

  }

  componentDidMount(){
    const opt = this.props.classroom.students
      .map(student => { return { value: student.id, label: student.name } })
    this.setState({
      classroom:{ students: opt}
    })
  }

  handleStudentSelect = (studentSelected) => {
    const values = studentSelected.map(stu => stu.value);
    this.setState({ studentSelected: values });

    const resultStudents = this.props.classroom.students.filter(option => 
      !values.some(values => option.id === values))

    this.setState({ classroom: { students: resultStudents}})
  }

  render(){
    const { disabled, classroom, studentSelected } = this.state
    const value = studentSelected && studentSelected.value;
    return(
      <div>
        <h1>Edit your clasroom "{this.props.classroom.title}"</h1>

        <div>
          <h2>Select students for delete to this classroom</h2>
          <Select
          className="mt-2"
          value={value}
          onChange={this.handleStudentSelect}
          options={classroom.students}
          isMulti
        />
        </div>

      </div>
    )
  }
}

export default withAuthConsumer(EditClassroom)
