import React, { Component } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import classroomService from '../../services/classroom-service'
class DeleteColumn extends Component {
  state = {
    show: false
  }

  handleChange = (event) => {
    const { name, value } = event.target;

    this.setState({ 
      classroom: {
        ...this.state.classroom,
        [name]: value
      }
    })
  }

  deleteColumn = (event) => {
    event.preventDefault();

    classroomService.deleteClassroom(this.state.classroom.classroomId)
    .then(this.props.fetchClassrooms)
    .then( this.setState({ show: false}))
  }

  handleShow = () => {
    this.setState({ show: true });
  }
  handleClose = () => {
    this.setState({ show: false });
  }

  render() {
    return(
      <div className="mx-auto">
        <Button variant="link" className="text-black-50 mt-3" onClick={this.handleShow} >
          Delete Classroom
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Delete Classroom</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            Are you sure? If you remove this column, the cards removes too
          </Modal.Body>
          <Modal.Footer>
            <Button variant="danger" onClick={this.deleteColumn}>
              Delete
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    )
  }
}

export default DeleteColumn;
