import React, { Component } from 'react';

var centesimas = 0;
var segundos = 0;
var minutos = 0;
var horas = 0;
function inicio () {
	control = setInterval(cronometro,10);
	document.getElementById("inicio").disabled = true;
	document.getElementById("parar").disabled = false;
	document.getElementById("continuar").disabled = true;
	document.getElementById("reinicio").disabled = false;
}
function parar () {
	clearInterval(control);
	document.getElementById("parar").disabled = true;
	document.getElementById("continuar").disabled = false;
}
function reinicio () {
	clearInterval(control);
	centesimas = 0;
	segundos = 0;
	minutos = 0;
	horas = 0;
	Centesimas.innerHTML = ":00";
	Segundos.innerHTML = ":00";
	Minutos.innerHTML = ":00";
	Horas.innerHTML = "00";
	document.getElementById("inicio").disabled = false;
	document.getElementById("parar").disabled = true;
	document.getElementById("continuar").disabled = true;
	document.getElementById("reinicio").disabled = true;
}
function cronometro () {
	if (centesimas < 99) {
		centesimas++;
		if (centesimas < 10) { centesimas = "0"+centesimas }
		Centesimas.innerHTML = ":"+centesimas;
	}
	if (centesimas == 99) {
		centesimas = -1;
	}
	if (centesimas == 0) {
		segundos ++;
		if (segundos < 10) { segundos = "0"+segundos }
		Segundos.innerHTML = ":"+segundos;
	}
	if (segundos == 59) {
		segundos = -1;
	}
	if ( (centesimas == 0)&&(segundos == 0) ) {
		minutos++;
		if (minutos < 10) { minutos = "0"+minutos }
		Minutos.innerHTML = ":"+minutos;
	}
	if (minutos == 59) {
		minutos = -1;
	}
	if ( (centesimas == 0)&&(segundos == 0)&&(minutos == 0) ) {
		horas ++;
		if (horas < 10) { horas = "0"+horas }
		Horas.innerHTML = horas;
	}
}



class Timer extends Component{





  redner(){
    return(

      <div className="tab-content" id="nav-tabContent">

        <div className="tab-pane fade show active" id="nav-home" role="tabpanel" aria-labelledby="nav-home-tab">
          <div className="d-flex justify-content-center p-2">
              <div className="crono-num" id="Horas">00</div>
              <div className="crono-num" id="Minutos">:00</div>
              <div className="crono-num" id="Segundos">:00</div>
              <div className="crono-num" id="Centesimas">:00</div>
          </div>
          <div className="d-flex justify-content-between mx-2">
              <input type="button" className="btn btn-outline-info btn-sm" id="inicio" value="Start &#9658;" onclick="inicio();"/>
              <input type="button" className="btn btn-outline-info btn-sm" id="parar" value="Stop &#8718;" onclick="parar();" disabled/>
              <input type="button" className="btn btn-outline-info btn-sm" id="continuar" value="Continue &#8634;" onclick="inicio();" disabled/>
              <input type="button" className="btn btn-outline-info btn-sm" id="reinicio" value="Reset &#8635;" onclick="reinicio();" disabled/>
          </div>         
        </div>




  </div>

    )
  }
}

export default Timer;
