import React, {Component} from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import { Modal, Button } from 'react-bootstrap';
import boardService from '../../services/board-service'


// const queryString = require('query-string');

const validators = {
    title:     v => v.length > 0,
    description:    v => v.length > 0,
    position: v => v.length > 0,
    column: v => v.length > 0
  }
class CreateCard extends Component {

    state = {
        card: {
          title: '',
          description: '',
          position: '',
          column: '',
          attach:'',
          filePreview:''
        },
        errors: {
          title: true,
          description: true,
          position: true
        },
        touch: {},
        onSubmit: false,
        show: false
      }

      handleChange = (e) => {
        const { name, value, files } = e.target;

        const isValid = validators[name] === undefined || validators[name](value);

        this.setState({
          card: {
            ...this.state.card,
            [name]: (files && files[0]) ? files[0] : value
          },
          errors: {
            ...this.state.errors,
            [name]: !isValid
          } 

        })
      }

      handleBlur = (e) => {
        this.setState({
          touch: {
            ...this.state.touch,
            [e.target.name]: true
          }
        })
      }



    postCard = (e) => {
      e.preventDefault();

      const cardData = {
        ...this.state.card
      }

      boardService.createCard(cardData)
        .then(() => this.setState({ onSubmit: true }))
    }

    handleClose = () => {this.setState({ show: false });}

    handleShow = () => {this.setState({ show: true });}

render(){

    const isError = Object.values(this.state.errors).some(error => error);
    const { card, errors, touch, show } = this.state;

    return (
      <>
        <Button variant="outline-secondary" className="fas fa-plus m-2 rounded-pill" onClick={this.handleShow}></Button>

        <Modal show={show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Create a new card</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <form onSubmit={this.createColumn}>
              <div className="form-group">
                <label htmlFor="nameClass">Title</label>
                <input type="text" 
                  className={`form-control form-control-sm ${touch.title ? (errors.title ? 'is-invalid' : 'is-valid') : ''}`} 
                  id="nameClass" 
                  placeholder="Ex. Exams for training..."
                  value={card.title} 
                  onChange={this.handleChange}
                  onBlur={this.handleBlur}
                  name="title"
                  autoComplete="off"
                  />
              </div>

              <div className="form-group">
                <label htmlFor="nameClass">Description</label>
                <input type="text" 
                  className={`form-control form-control-sm ${touch.title ? (errors.title ? 'is-invalid' : 'is-valid') : ''}`} 
                  id="nameClass" 
                  placeholder="Ex. Exams for training..."
                  value={card.title} 
                  onChange={this.handleChange}
                  onBlur={this.handleBlur}
                  name="title"
                  autoComplete="off"
                  />
              </div>

            </form>
          </Modal.Body>
            <Modal.Footer>
              <Button variant="info" onClick={this.createColumn}>Create</Button>
            </Modal.Footer>
        </Modal>
      </>
      //   <div className="container col-4 mt-5">
      //   <form onSubmit={this.postCard}>
      //     <FormField title="Title" name="title" value={this.state.card.title}
      //       onChange={this.handleChange} icon="fas fa-user" error={this.state.errors.title}
      //       onBlur={this.handleBlur} touch={this.state.touch.title}/>

      //     <FormField title="Description" name="description" value={this.state.card.description}
      //       onChange={this.handleChange} icon="fas fa-at" type="text"
      //       error={this.state.errors.description} onBlur={this.handleBlur} touch={this.state.touch.description} />

      //     <FormField title="Position" name="position" value={this.state.card.position}
      //       onChange={this.handleChange} icon="fas fa-key" type="Number"
      //       error={this.state.errors.position} onBlur={this.handleBlur} touch={this.state.touch.position} />


      //     {this.state.card.attach && <img className="img-upload" src={URL.createObjectURL(this.state.card.attach)} alt="..."/>}

      //       <button type="button" className="bot btn btn-outline-primary">
      //         <input 
      //             type="file" 
      //             className="custom-file-input" 
      //             name="attach" id="attach" 
      //             aria-describedby="attach"
      //             onChange={this.handleChange}
      //             onBlur={this.handleBlur}
      //             lang="es"/> <i className="abc fas fa-plus"></i>
      //       </button>

      //     <div className="control">
      //       <button className="btn btn-primary mt-3" disabled={isError}>
      //         <span>+ Add</span>
      //       </button>
      //     </div>
      //   </form>
      // </div>

    )
}}


export default withAuthConsumer(CreateCard) ;
