import React, { Component } from 'react';
import { Modal, Button } from 'react-bootstrap';
import boardService from '../../services/board-service';
class DeleteCard extends Component {
  state = {
    show: false
  }


  deleteCard = () => {

    boardService.deleteCard(this.props.id)
    .then(this.props.fetchCards)
    .then( this.setState({ show: false}))
  }

  handleShow = () => {
    this.setState({ show: true });
  }
  handleClose = () => {
    this.setState({ show: false });
  }

  render() {
    return(
      <div className="">
        <Button variant="link" className="text-black-50 mt-3" onClick={this.handleShow} >
          x
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Delete Card</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            Are you sure? If you remove this card, you remove all information relative to de card
          </Modal.Body>
          <Modal.Footer>
            <Button variant="danger" onClick={this.deleteColumn}>
              Delete
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    )
  }
}

export default DeleteCard;
