import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import { Link } from 'react-router-dom';
import NewGroup from './'


class Groups extends Component {
  state = {
    classrooms: []
  }

  fetchClassrooms = () => {

  }

  componentDidMount(){

  }

  handleClick = (classroom) =>{
    this.props.onClassroomChanged(classroom)
  }



  render() {

    return(
      <div>
        <div className="classroom">
          {this.state.classrooms.map(classroom => (
            <Link 
              className="btn btn-info classroom-btn" 
              key={classroom.id} 
              to="/classroom"
              onClick={() => this.props.onClassroomChanged(classroom)}
              >
              <h5 className="mb-0">{classroom.title}</h5>
              <div>
                <small className="text-white-50">STUDENTS</small>
                <h6 className="mb-0">{classroom.students.length || "0"}</h6>
              </div>
            </Link>
          ))}
          <NewGroup fetchClassrooms={this.fetchClassrooms}/>
          <DeleteGroup listClassrooms={this.state.classrooms} fetchClassrooms={this.fetchClassrooms}/>
        </div>
      </div>
    )
  }
}

export default withAuthConsumer(Groups);
