import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import chatService from '../../services/chat-service';



class Conversation extends Component {

  state = {
    message: {
      content: '',
      recipient: '',
      sender:this.props.localUser.id,

    }
  }

  componentDidMount(){

  }

  createMessage = () =>{
    chatService.createMessages(this.state.message)
  }

  render() {


    return (
      <div className="conversation">
        <div className="server-card">
            <img src={this.props.imageURL} alt=""/>
            <p>{this.props.name}</p>
            <Button onClick={this.props.hideConversation}>x</Button>
        </div>
        <div className="chat">
            <div className="conversation">
                <div className="message-r">
                    Texto del receptor
                </div>
                <div className="message-s">
                    Texto del emisor bla bla bla
                </div>
            </div>
            <form className="write">
                <div className="input-group mb-3">
                    <input type="text" 
                    className="form-control" 
                    placeholder="Escribe un mensaje..." 
                    aria-label="Escribe un mensaje..." 
                    aria-describedby="button-addon2"/>
                    <div className="input-group-append">
                        <button className="btn btn-outline-info" type="button" id="button-addon2"><i className="fas fa-space-shuttle"></i></button>
                    </div>
                </div>
            </form>
        </div>
      </div>
    );
  }
}

export default Conversation ;
