import React, { Component } from 'react';
import { Button, Modal, Form } from 'react-bootstrap';
import classroomService from '../../services/classroom-service';


class CheckList extends Component {
    state = {
      show: false,
      date: '',
      checklist: {
        observations: '',
        assistants: []
      }

    }

    handleClose = () => {
        this.setState({ show: false });
      }

    handleShow = () => {
        this.setState({ 
          show: true,
          checklist: {
            assistants: this.props.studentSelected
          } 
        });
        this.setState({ checklist:{
          assistants: this.props.studentSelected
        } })
    }

    componentDidMount(){
      this.handleDate();
    }

    handleDate = () => {
      const date = new Date().toDateString();
      this.setState({ date });
    }

    handleSubmit = (event) => {
      event.preventDefault();

    classroomService.createClassroom()
    .then(this.setState({ show: false }))
    }


    render(props) {
        return (
            <>
                <Button variant="outline-secondary rounded-pill"  onClick={this.handleShow}>
                    Check list
                </Button>

                <Modal show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title className="d-flex flex-column">Check list today 
                        <small className="text-secondary">{this.state.date}</small>
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                      <div className="list-info">
                        <p className="text-info">Students in classroom</p>
                        <h1 className="list-total">{this.props.totalStudent}</h1>
                        <h1 className="list-num">{this.props.studentSelected.length}</h1>   
                      </div>
                      <Form onSubmit={this.handleSubmit}>
                        <label htmlFor="observationsText">Observations</label>
                        <textarea className="form-control" 
                          id="observationsText" 
                          rows="3" 
                          name="observations"
                          />
                      </Form>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                    </Button>
                        <Button variant="primary" onClick={this.handleClose}>
                            Save Changes
                    </Button>
                    </Modal.Footer>
                </Modal>
            </>
        )
    }


}

export default CheckList;
