  handleSubmit = () => {

  }

  isValid = () => {

  }

  render(){

    return(
      <div class="login shadow-sm">
        <div class="login-body">
            <img class="w-75 mx-auto my-3" src="./logo coolScool.svg" alt=""/>
            <form>
                <div class="form-group">
                    <label for="exampleInputEmail1">Email</label>
                    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Email"/>
                </div>
                <div class="form-group">
                    <label for="exampleInputPassword1">Contraseña</label>
                    <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Contraseña"/>
                </div>
                <div class="form-group">
                    <label for="exampleInputPassword1">Nombre y Apellidos</label>
                    <input type="text" class="form-control" id="exampleInputPassword1" placeholder="Nombre completo"/>
                </div>
                <div class="form-check form-check-inline">
                        <input class="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio1" value="option1"/>
                        <label class="form-check-label" for="inlineRadio1">Profesor</label>
                    </div>
                    <div class="form-check form-check-inline">
                        <input class="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio2" value="option2"/>
                        <label class="form-check-label" for="inlineRadio2">Alumno</label>
                </div>
                <button type="submit" class="btn btn-info w-100 mt-3">Entrar</button>

            </form>
            <p class="mx-auto mt-4">Si ya tienes cuenta <a href=""> ENTRA </a>en tu espacio</p>

                </div>
      </div>
    )
  }
}

export default Register;
