import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import classroomService from '../../services/classroom-service';
import { Button, Modal, FormControl, InputGroup } from 'react-bootstrap';



class AddStudent extends Component {
  state = {
    students: []
  }

  // fetchStudents = () => {
  //   classroomService.detailClassroom(this.props.classroom.id)
  //     .then(students => this.setState({ students }))
  // }

  // componentDidMount(){

  // }

  handleClose = () => {
    this.setState({ show: false });
  }

  handleShow = () => {
    this.setState({ show: true });
  }


  render() {

    return(
      <>
        <Button variant="outline-info ml-3" onClick={this.handleShow}>
          +
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Add News Students</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <InputGroup className="mb-3">
              <FormControl
                placeholder="Student email..."
                aria-label="Recipient's username"
                aria-describedby="basic-addon2"
              />
              <InputGroup.Append>
                <Button variant="outline-secondary">+ Add</Button>
              </InputGroup.Append>
            </InputGroup>
            <ul>
              <li> Nombre y mail del alumno</li>
            </ul>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={this.handleClose}>
              Save students
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    )
  }
}

export default withAuthConsumer(AddStudent);
