import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import calendarService from '../../services/calendar-service';

import FullCalendar from 'fullcalendar-reactwrapper';
import corePlugin from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import listPlugin from '@fullcalendar/list';


import CreateEvent from './CreateEvent';
import EditEvent from './EditEvent';
import { Modal, Button } from 'react-bootstrap';

class Calendar extends Component {

  state = {
    events:[],
    show: false,
    oneEvent:{}
    }

    fetchEvents = () => {
      calendarService.listEvent(this.props.classroom.id)
      .then(events => this.setState({ events }))
    }

    componentDidMount(){
      this.fetchEvents();
    }

    opHandler = (oneEvent) => {
      this.setState({ show: true,
      oneEvent: oneEvent
      })
      console.log("eooo", oneEvent)

    }

    handleClose = () => {this.setState({ show: false });}

    handleShow = (event) => {this.setState({ show: true });}


  render() {

    return(
      <div className="mt-4 p-1">
        <FullCalendar
        id = "calendar"
        header = {{
            left: 'prev,next today myCustomButton',
            center: 'title',
            right: 'month,basicWeek,basicDay'
        }}


        eventClick={this.opHandler}
        navLinks= {true} // can click day/week names to navigate views
        editable= {true}
        eventLimit= {true} // allow "more" link when too many events
        events = {this.state.events}	
        />
        <div className="d-flex justify-content-center mt-3">
          <CreateEvent fetchEvents={this.fetchEvents}/>
        </div>

        <Modal show={this.state.show} onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>E</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <form onSubmit={this.handleSubmit}>
              <div className="form-group">
                <label htmlFor="titleEvent">Title</label>
                <input type="text" 
                  className="form-control" 
                  id="titleEvent"
                  name="title"
                  onChange={this.handleChange}
                  onBlur={this.handleBlur}
                  value={this.state.oneEvent.title}
                  />

              </div>

              <div className="form-check form-check-inline">
                <input className="form-control" 
                type="radio" 
                name="role" 
                id="roleEvent" 
                value="classroom"
                checked={this.state.oneEvent.role === "classroom"}
                onChange={this.handleChange}
                />
                <label className="form-check-label" htmlFor="roleRegister">Just this classroom</label>
              </div>
              <div className="form-check form-check-inline">
                <input className={`form-check-input ${this.state.oneEvent.role && 'custom-control-input'}`} 
                  type="radio" 
                  name="role" 
                  id="roleEvent2" 
                  value="school"
                  checked={this.state.oneEvent.role === "school"}
                  onChange={this.handleChange}
                  />
                <label className="form-check-label" htmlFor="roleEvent2">All school</label>
                <div className="invalid-feedback">{this.state.oneEvent.role}</div>
              </div>

              <div id="collapse-date">
                <div className="m-2">
                  <div className="mt-3">
                    <label htmlFor="dateStart" className="col-form-label">Start</label>
                    <input type="date" className="ml-3" id="dateStart"
                    name="start" 
                    step="1" 
                    value={this.state.oneEvent.start}
                    placeholder={this.state.oneEvent.start}
                    onChange={this.handleChange}
                    />
                  </div>
                  <div className="mt-3">
                    <label htmlFor="dateEnd" className="col-form-label">End</label>
                    <input className="ml-3" type="date" id="dateEnd"
                    step="1"
                    name="end" 
                    value={this.state.oneEvent.end}
                    onChange={this.handleChange}
                    />
                  </div>
                </div>
              </div>
            </form>
            </Modal.Body>
              <Modal.Footer>
                <Button variant="info" onClick={this.createEvent}>Edit Event</Button>
                <Button variant="danger" onClick={this.createEvent}>Delete Event</Button>
              </Modal.Footer>
          </Modal>
      </div>
    )
  }
}

export default withAuthConsumer(Calendar);
