import React, { Component } from 'react';
import boardService from '../../services/board-service'
import { Modal, Button } from 'react-bootstrap';

class Card extends Component {

  state = {
    show: false
  }


  handleClose = () => {this.setState({ show: false });}

  handleShow = () => {this.setState({ show: true });}

  render(props) {
    const { show } = this.state;

    const date = props.createdAt.slice(0, 10)


    return(

      <div className="card-item mt-2">
        {/* <Link card={props.title} className="" to="./card" data-target=".bd-card-modal-xl">
            <div className="card-header p-2 bg-secondary">
                <p className="text-light">{props.title}</p> 
            </div>
            <div className="card-body p-2 d-flex justify-content-between align-items-center text-info bg-white">
                <p className="card-text-panel">{date}</p>
                <div className="d-flex flex-row align-items-center">
                  {props.attachFiles &&
                  <p className="card-text-panel mr-2"><i className="fas fa-paperclip"></i></p>}
                  {props.accountPay && 
                  <p className="card-text-panel mr-2"><i className="fas fa-euro-sign"></i></p>}
                  {props.dateStart &&
                  <p className="card-text-panel mr-2"><i className="fas fa-calendar"></i></p>}
                  <DeleteCard />
                </div>
            </div>
        </Link> */}
        <Button onClick={this.handleShow} className="" data-target=".bd-card-modal-xl">
            <div className="card-header p-2 bg-secondary">
                <p className="text-light">{props.title}</p> 
            </div>
            <div className="card-body p-2 d-flex justify-content-between align-items-center text-info bg-white">
                <p className="card-text-panel">{date}</p>
                <div className="d-flex flex-row align-items-center">
                  {props.attachFiles &&
                  <p className="card-text-panel mr-2"><i className="fas fa-paperclip"></i></p>}
                  {props.accountPay && 
                  <p className="card-text-panel mr-2"><i className="fas fa-euro-sign"></i></p>}
                  {props.dateStart &&
                  <p className="card-text-panel mr-2"><i className="fas fa-calendar"></i></p>}
                  <DeleteCard />
                </div>
            </div>
        </Button>

        <Modal show={show} onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Create a new Column for your board</Modal.Title>
            </Modal.Header>
            <Modal.Body>

            </Modal.Body>
              <Modal.Footer>
                <Button variant="info" onClick={this.createColumn}>Create</Button>
              </Modal.Footer>
          </Modal>
      </div>
    )
}}

export default Card;
