import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import classroomService from '../../services/classroom-service';
import { Form, Button } from 'react-bootstrap';
import { Select } from 'react-select-plus';

class EditClassroom extends Component{

  state = {
    disabled: true,
    classroom: {
      students: [this.props.classroom.students],
      title: this.props.classroom.title,
      accountPay: this.props.classroom.accountPay
    }

  }

  handleChange = (event) => {

    const { value } = event.target;

    this.setState({ 
      studentSelected: value,
      disabled: false
    })
  }

  handleSubmit = (event) => {
    event.preventDefault();

    classroomService.editClassroom(this.state.classroom)

  }

  render(){
    const { disabled } = this.state
    return(
      <Select
        className="mt-2"
        key={name}
        name={name}
        value={subgroups['name']}
        onChange={this.handleSubgroupSelect}
        options={this.state.selectableOptions}
        isMulti
      />

    )
  }
}

export default withAuthConsumer(EditClassroom)
