import React from 'react';
import { Chat, Channel, ChannelList, Window } from 'stream-chat-react';
import { ChannelHeader, MessageList } from 'stream-chat-react';
import { MessageInput, Thread } from 'stream-chat-react';
import { StreamChat } from 'stream-chat';

import 'stream-chat-react/dist/css/index.css';

const chatClient = new StreamChat('qk4nn7rpcn75');
const userToken = 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjoiZnJvc3R5LXJpdmVyLTIifQ.0AsawVJceEww5tm4G2B43m44CB8TYOEW7PlXL2-Kydo';

chatClient.setUser(
  {
    id: 'frosty-river-2',
    name: 'Frosty river',
    image: 'https://getstream.io/random_svg/?id=frosty-river-2&name=Frosty+river'
  },
  userToken,
);

const filters = { };
const sort = { last_message_at: -1 };
const channels = chatClient.queryChannels(filters, sort);

const App = () => (
  <Chat client={chatClient} theme={'messaging light'}>
    <ChannelList channels={channels}/>
    <Channel>
      <Window>
       <ChannelHeader />
        <MessageList />
        <MessageInput />
      </Window>
      <Thread />
    </Channel>
  </Chat>
);

export default App;
