import React, { Component } from 'react';
import { Link, Redirect, withRouter } from 'react-router-dom'
import authService from '../../services/auth-service';
import { withAuthConsumer } from '../../contexts/AuthStore';
import Drawer from 'react-motion-drawer';
import { Button } from 'react-bootstrap';



class FooterNav extends Component {

  state = {
    openLeft: false,
    drawerStyle: `
{
  "background": "#F9F9F9",
  "boxShadow": "rgba(0, 0, 0, 0.188235) 0px 10px 20px, rgba(0, 0, 0, 0.227451) 0px 6px 6px"
}`,
    relativeWidth: false,
    width: -300,
    noTouchOpen: false,
    noTouchClose: false,
  }

  handleLogout = () => {
    authService.logout()
      .then( () => {
        this.props.onUserChanged({})
        this.props.history.push('/login')
      })
  }

  setWidth = e => {
    this.setState({
      width: Number(e.target.value) || e.target.value
    });
  };

  setTouch = e => {
    this.setState({
      [e.target.name]: !e.target.checked
    })
  }

  setDrawerStyle = e => {
    e.preventDefault()
    this.setState({
      drawerStyle: this.drawerStyleRef.value
    })
  }



  render() {
    const {
      drawerStyle: stringDrawerStyle,
      openLeft,
      noTouchOpen,
      noTouchClose
    } = this.state;

    let drawerStyle = {}
    try {
      drawerStyle = JSON.parse(stringDrawerStyle)
    } catch (err) {
      console.error('Error parsing JSON: ', err)
    }

    const drawerProps = {
      overlayColor: "rgba(255,255,255,0.6)",
      drawerStyle
    };


    return(
      <div className="container-footerNav">
      <span
        className=""
        onClick={() =>
          this.setState({ openLeft: !openLeft })}
      > 
        <i className="fa fa-bars" />
      </span>
       <Drawer
          {...drawerProps}
          width={this.state.width}
          fadeOut
          open={openLeft}
          onChange={open => this.setState({ openLeft: open })}
          noTouchOpen={noTouchOpen}
          noTouchClose={noTouchClose}
        >
          <div style={{ padding: "2em" }}>
            <h3>Navigation</h3>
          </div>
        </Drawer>


      </div>


    )
  }
}

export default withAuthConsumer(withRouter(FooterNav)) ;
