import React, { Component } from 'react';
import classroomService from '../../services/classroom-service'

const validations = { title: (value) => value.length > 0 && value.length < 20}
class Home extends Component {
  state = {
    classroom: {
      title: '',
      teachers: ''
    },
    errors: {
      title: true
    },
    touch: {},
    toHome: false
  }

  handleChange = (event) => {
    const { name, value } = event.target;

    this.setState({ 
      card: {
        ...this.state.classroom,
        [name]: value
      },
      errors: {
        ...this.state.errors,
        [name]: validations[name] && validations[name](value)
      }
    })
  }

  handleBLur = (event) => {
    const { name } = event.target;

    this.setState({
      ...this.state.touch,
      [name]: true
    })
  }

  createClassroom = (event) => {
    event.preventDefault();

    const classroomData = {
      ...this.state.classroom,
      teachers: this.user //falta contenido para que cargue al que está en la sesión
    }
    classroomService.createClassroom(classroomData)
      .then(() => this.setState({ toHome: true }))
  }



  render() {
    const { toBoard, card, touch, errors } = this.state;
    return(
      <div>
        <div className="classroom">
          <button type="button" className="btn btn-info classroom-btn">
            <h5 className="mb-0">Nombre Clase</h5>
            <div>
              <small className="text-white-50">Nº ALUMNOS</small>
              <h6 className="mb-0">20</h6>
            </div>
          </button>
          <button type="button" className="btn btn-outline-info new-classroom-btn" data-toggle="modal" data-target="#newCardModal">
            <div className="d-flex justify-content-center">
              <h6 className="mb-0">+ Nueva Clase</h6>
            </div>
          </button>

          {/* <!-- Modal new classroom --> */}
          <div className="modal fade" id="newCardModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
            <div className="modal-dialog modal-dialog-centered" role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalCenterTitle">New Classroom</h5>
                  <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                <div className="modal-body">
                  <form onSubmit={this.createClassroom}>
                    <div className="form-group">
                      <label htmlFor="nameClass">Classroom name</label>
                      <input type="text" 
                        className="form-control" 
                        id="nameClass" 
                        placeholder="Ej. 5º Matemáticas"
                        value={classroom.title} 
                        onChange={this.handleChange}
                        onBlur={this.handleBlur}
                        name="title"
                        />
                    </div>
                  </form>
                </div>
                <div className="modal-footer">
                  <button type="submit" className="btn btn-info">Create</button>
                </div>
              </div>
            </div>
          </div>

          {/* <!-- Button delete className --> */}
          <button type="button" className="btn text-secondary" data-toggle="modal" data-target="#deleteClassModal">
            Delete Classroom
          </button>

          {/* <!-- Modal delete className --> */}
          <div className="modal fade" id="deleteClassModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">Borrar una clase</h5>
                  <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                  <div className="modal-body">
                    <div className="form-group">
                      <label htmlFor="exampleFormControlSelect1">Seleccione la clase que quiere borrar</label>
                      <select className="form-control" id="exampleFormControlSelect1">
                          <option>Classroom</option>
                      </select>
                    </div>
                    <div className="form-check form-check-inline">
                      <input className="form-check-input" type="checkbox" id="inlineCheckbox1" value="option1"/>
                      <label className="form-check-label" htmlFor="inlineCheckbox1">Confirmo que quiero borrar la clase selecionada</label>
                    </div>
                  </div>
                  <div className="modal-footer">
                    <button type="button" className="btn btn-danger">Delete</button>
                  </div>
                </div>
              </div>
          </div>
        </div>
      </div>
    )
  }
}

export default Home;
