import React, { Component } from 'react';
import groupService from '../../services/group-service'
import { withAuthConsumer } from '../../contexts/AuthStore';
import { Modal, Button } from 'react-bootstrap';

import Select from 'react-select';


const validations = {
  title: (value) => {
  let message;
  if(!value){
    message = 'Some message is required'
  }
  return message;
}}
class NewGroup extends Component {

  state = {
    group: {
      title: '',
      subgroups: {
        'group-1': []
      }
    },
    errors: {
      title: validations.title()
    },
    touch: {},
    show: false,
    selectedOption: '',
    selectedOption2: '',
    options:[]
  }

  handleChange = (event) => {

    const { name, value } = event.target;

    this.setState({ 
      classroom: {
        ...this.state.classroom,
        [name]: value
      },
      errors: {
        ...this.state.errors,
        [name]: validations[name] && validations[name](value)
      }
    })
  }

  handleSubgroupSelect = (options, { name }) => {
    const value = options.map(o => o.value)

    this.setState({
      group: {
        ...this.state.group,
        subgroups: {
          ...this.state.group.subgroups,
          [name]: value
        }
      },
    });
    // console.log(selectedOption)
    // const newOptions = this.state.options.filter(selectedOption =>  selectedOption)
    // //const newOptions = this.state.options.splice(selectedOption, 1)
    // //const newOptions = this.state.options.delete(selectedOption)
    // console.log(newOptions)
    // this.setState({ options: newOptions})//como decirle que al array de opciones le quite la seleccionada
  }

  handleBLur = (event) => {
    const { name } = event.target;

    this.setState({
      ...this.state.touch,
      [name]: true
    })
  }

  handleAddSubgroup = () => {
    const name = `group-${Object.keys(this.state.group.subgroups).length + 1}`
    this.setState({
      group: {
        ...this.state.group,
        subgroups: {
          ...this.state.group.subgroups,
          [name]: []
        }
      }
    })
  }

  createClassroom = (event) => {
    event.preventDefault();

    const classroomData = {
      ...this.state.classroom,
      teachers: this.props.user.id
    }
    groupService.createClassroom(classroomData)
    .then(this.props.fetchClassrooms)
    .then(this.setState({ show: false }))
  }

  handleClose = () => {this.setState({ show: false });}

  handleShow = () => {this.setState({ show: true });}

  componentDidMount(){
    const opt = this.props.classroom.students
      .map(student => { return { value: student.name, label: student.name } })
    this.setState({ options: opt})
  }

  render() {
    const { group, touch, errors, show } = this.state;
    const subgroups = group.subgroups;


    console.log(subgroups)

    return(
      <div>
          <Button variant="outline-info new-classroom-btn" onClick={this.handleShow}>+ New Group</Button>

          <Modal show={show} onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Create a new Group</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <form onSubmit={this.createClassroom}>
                <div className="form-group">
                  <label htmlFor="nameGroup">Group name</label>
                  <input type="text" 
                    className={`form-control form-control-sm ${touch.title ? (errors.title ? 'is-invalid' : 'is-valid') : ''}`} 
                    id="nameGroup"
                    value={group.title} 
                    onChange={this.handleChange}
                    onBlur={this.handleBlur}
                    name="title"
                    autoComplete="off"
                    />
                </div>
                <Button className="btn" onClick={this.handleAddSubgroup}>Add Subgroup</Button>
                {Object.keys(subgroups).map(name =>
                  (<Select
                    key={name}
                    name={name}
                    value={subgroups['name']}
                    onChange={this.handleSubgroupSelect}
                    options={this.state.options}
                    isMulti
                  />)
                  )}
              </form>
            </Modal.Body>
              <Modal.Footer>
                <Button variant="info" onClick={this.createClassroom}>Create</Button>
              </Modal.Footer>
          </Modal>
      </div>
    )
  }
}

export default withAuthConsumer(NewGroup) ;
