import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import classroomService from '../../services/classroom-service';
import { Link, NavLink } from 'react-router-dom';
import Conversation from './Conversation';
import { Button } from 'react-bootstrap';


class Chat extends Component {
  state = {
    users: this.props.classroom.students,
    messages:[],
    showConversation: true,
    showInbox: false,
    selected: ''
  }

  fetchClassrooms = () => {

  }

  componentDidMount(){

  }

  handleShowConversation = (student) => {
    console.log(student)
    this.setState({ 
      showConversation: false,
      showInbox: true,
      selected: student

    });
  }

  handleHideConversation = () => {
    this.setState({ 
      showConversation: true,
      showInbox: false
    });
  }



  render() {

    return(
      <div>
          <div className="inbox" hidden={this.state.showInbox}>
          {this.state.students.map(student => (
            <Button 
              className="btn inbox-btn" 
              key={student.id} 
              student={student.id}
              onClick={() => this.handleShowConversation(student)}
            >
            <div className="d-flex flex-row align-items-center">
              <img className="inbox-btn-img" src={student.imageURL} alt="student-avatar"/>
              <p className="mb-0 ml-3">{student.name} {student.surname}</p>
            </div> 
            </Button>
          ))}
          </div>
          <div className="conversation" hidden={this.state.showConversation}>
            <Conversation {...this.state.selected} hideConversation={this.handleHideConversation}/>
          </div>
      </div>
    )
  }
}

export default withAuthConsumer(Chat);
