import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import classroomService from '../../services/classroom-service';
import { Form, Button } from 'react-bootstrap';
import  Select from 'react-select';

const validations = {
  title: (value) => {
  let message;
  if(!value){
    message = 'Some title is required'
  }
  return message;
},
accountPay: (value) => {
  let message;
  if(!value){
    message = 'Some title is required'
  }
  return message;
}}



class EditClassroom extends Component{

  state = {
    disabled: true,
    classroom: {
      students: [],
      title: this.props.classroom.title,
      accountPay: this.props.classroom.accountPay
    },
    errors: {
      title: validations.title(),
      accountPay: validations.accountPay()
    },
    studentSelected:[],
    touch: {}

  }

  handleChange = (event) => {

    const { value } = event.target;

    this.setState({ 
      studentSelected: value,
      disabled: false
    })
  }
  handleBLur = (event) => {
    const { name } = event.target;

    this.setState({
      ...this.state.touch,
      [name]: true
    })
  }

  handleSubmit = (event) => {
    event.preventDefault();

    classroomService.editClassroom(this.state.classroom)

  }

  componentDidMount(){
    const opt = this.props.classroom.students
      .map(student => { return { value: student.id, label: student.name } })
    this.setState({
      classroom:{ students: opt}
    })
  }

  handleStudentSelect = (studentSelected) => {
    const values = studentSelected.map(stu => stu.value);
    this.setState({ studentSelected: values });

    const resultStudents = this.props.classroom.students.filter(option => 
      !values.some(values => option.id === values))

    this.setState({ classroom: { students: resultStudents}})
  }

  render(){
    const { disabled, classroom, studentSelected } = this.state
    const value = studentSelected && studentSelected.value;
    return(
      <div>
        <h1>Edit your clasroom "{this.props.classroom.title}"</h1>

        <div>
          <label htmlFor="studentSelector">Select students for delete to this classroom</label>
          <Select
          id="studentSelector"
          className="mt-2"
          value={value}
          onChange={this.handleStudentSelect}
          options={classroom.students}
          isMulti
        />
        <label htmlFor="titleClassroom">Change the classroom title</label>
        <input type="text" 
          className={`form-control form-control-sm ${touch.title ? (errors.title ? 'is-invalid' : 'is-valid') : ''}`} 
          id="titleClassroom"
          value={group.title} 
          onChange={this.handleChange}
          onBlur={this.handleBlur}
          name="title"
          autoComplete="off"
          />
        </div>

      </div>
    )
  }
}

export default withAuthConsumer(EditClassroom)
