import React, { Component } from 'react';
import { Link, Redirect, withRouter } from 'react-router-dom'
import authService from '../../services/auth-service';
import { withAuthConsumer } from '../../contexts/AuthStore';
import Drawer from 'react-motion-drawer';



class FooterNav extends Component {

  state = {
    openLeft: false,
    openRight: false,
    drawerStyle: `
{
  "background": "#F9F9F9",
  "boxShadow": "rgba(0, 0, 0, 0.188235) 0px 10px 20px, rgba(0, 0, 0, 0.227451) 0px 6px 6px"
}`,
    relativeWidth: false,
    width: 300,
    noTouchOpen: false,
    noTouchClose: false,
  }

  handleLogout = () => {
    authService.logout()
      .then( () => {
        this.props.onUserChanged({})
        this.props.history.push('/login')
      })
  }

  setWidth = e => {
    this.setState({
      width: Number(e.target.value) || e.target.value
    });
  };

  setTouch = e => {
    this.setState({
      [e.target.name]: !e.target.checked
    })
  }

  setDrawerStyle = e => {
    e.preventDefault()
    this.setState({
      drawerStyle: this.drawerStyleRef.value
    })
  }



  render() {


    return(
      <div className="container-footerNav">
      <Drawer open={false} width={300} onChange={"wwrie"}>
          { val =>
            <ul style={{ opacity: 300 / val }}>
              <li>Home</li>
              <li>About</li>
              <li>Settings</li>
            </ul>
          }
        </Drawer>
    </div>

    )
  }
}

export default withAuthConsumer(withRouter(FooterNav)) ;
