import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import groupService from '../../services/group-service'
import NewGroup from './NewGroup'
import DeleteGroup from './DeleteGroup'


class Groups extends Component {
  state = {
    groups: []
  }

  fetchClassrooms = () => {
    groupService.listGroups(this.props.classroom.id)
    .then(groups => this.setState({ groups: groups}))
  }

  componentDidMount(){
    this.fetchClassrooms()
  }

  handleClick = (classroom) =>{
    this.props.onClassroomChanged(classroom)
  }



  render() {

    return(
      <div>
        <div className="classroom">
          {this.state.groups.map(group => (
            <div className="btn btn-info classroom-btn" key={group.id}>
              <h6 className="mb-0">{group.title}</h6>
                {Object.keys(group.subgroups).map(subgroup => (
                    <p></p>
                ))}
              <div>

              </div>
            </div>
          ))}
          <NewGroup fetchClassrooms={this.fetchClassrooms}/>
          {/* <DeleteGroup listClassrooms={this.state.classrooms} fetchClassrooms={this.fetchClassrooms}/> */}
        </div>
      </div>
    )
  }
}

export default withAuthConsumer(Groups);
