import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import classroomService from '../../services/classroom-service';
import { Form, Button } from 'react-bootstrap';




class DetailStudent extends Component {
  state = {
    student: ''
  }

  fetchStudent = () => {
    classroomService.detailStudent(this.props.match.url)
    .then(student=> console.log(student))
  }

  componentDidMount(){
    // console.log(this.props.match.url)
    // this.fetchStudent()
  }

  handleChange = (event) => {

    const { name, value } = event.target;

    this.setState({ 
      student: {
        [name]: value
      }
    })
  }


  handleSubmit = (event) => {
    event.preventDefault();

    const column = {
      ...this.state.column
    }
    classroomService.createColumn(column)
    .then(this.props.fetchColumns)
    .then(this.setState({ show: false }))
  }


  render() {

    return(
      <div className="container-columns">
       <Form.Group onSubmit={this.handleSubmit}>
          <Form.Label>Select a student</Form.Label>
          <Form.Control as="select" onChange={this.handleChange}>
          {this.props.classroom.students.map(student => 
            <option key={student.id} value={student.id} name="student">{student.name} {student.surname}</option>
            )}

          </Form.Control>
          <Button type="submit">View</Button>
        </Form.Group>
      </div>
    )
  }
}

export default withAuthConsumer(DetailStudent);
