import React, { Component } from 'react';
import { Collapse, Button, ButtonGroup } from 'react-bootstrap';
import Chronometer from './Chronometer';

class Timer extends Component{

  state ={
    show: false,
    chronometer: false,
    pay: false,
    attach: false
  }

  render(){


    return(
      <>
      <ButtonGroup >
      <Button
        onClick={() => this.setState({ attach: !attach, pay: false, chronometer: false })}
        aria-controls="collapse-chronometer"
        aria-expanded={attach}
        variant="outline-secondary"
      >
        Attach
      </Button>
      <Button
        onClick={() => this.setState({ chronometer: !chronometer, pay: false, attach: false })}
        aria-controls="collapse-chronometer"
        aria-expanded={chronometer}
        variant="outline-secondary"
      >
        chronometer
      </Button>
    </ButtonGroup>
    <Collapse in={this.state.attach}>
      cronometro
    </Collapse>
    <Collapse in={this.state.chronometer}>
      temporizador
    </Collapse>

    </>
    )
  }
}

export default Timer;
