import React, { Component } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

class DeleteClassroom extends Component {
  state = {
    show: false
  }

  handleClose = () => {
    this.setState({ show: false });
  }

  handleShow = () => {
    this.setState({ show: true });
  }

  render() {
    const {  touch, show } = this.state;
    return(
      <div className="mx-auto">
      <Button variant="primary" onClick={this.handleShow}>
          Delete Classroom
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Delete Classroom</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <Form>
            <Form.Group controlId="exampleForm.ControlSelect1">
              <Form.Label>Select the classroom for delete</Form.Label>
              <Form.Control as="select">
              {this.state.classrooms.map(classroom => (<option>{this.props.listClassrooms}</option>))}

              </Form.Control>
            </Form.Group>
          </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={this.handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>


        </div>
    )
  }
}

export default DeleteClassroom;
