import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import classroomService from '../../services/classroom-service';
import { Form, Button } from 'react-bootstrap';




class DetailStudent extends Component {
  state = {
    studentSelected: '',
    student: {},
    hidden: true

  }

  getStudent = () => {
    classroomService.detailStudent(this.state.studentSelected)
    .then(student=> this.setState({ student: student }))
  }

  componentDidMount(){

  }

  handleChange = (event) => {

    const { value } = event.target;

    this.setState({ 
      studentSelected: value
    })
  }


  handleSubmit = (event) => {
    event.preventDefault();
    this.getStudent()
    this.setState({ hidden: false})
  }


  render() {
    const { student,hidden } = this.state
    return(

      <div className="container-columns">
       <Form.Group>
          <Form.Label>Select a student</Form.Label>
          <Form.Control as="select" onChange={this.handleChange}>
          <option>Select one ...</option>
          {this.props.classroom.students.map(student => 
            <option key={student.id} value={student.id} name="student">{student.name} {student.surname}</option>
            )}

          </Form.Control>
          <Button onClick={this.handleSubmit}>View</Button>
        </Form.Group>
        <div hidden={hidden}>
          <img src={student.imageURL} alt="student-avatar"/>
          <div>
            <h1>{student.name} {student.surname}</h1>
            <h2>{student.email}</h2>
          </div>

        </div>
      </div>
    )
  }
}

export default withAuthConsumer(DetailStudent);
