import React, { Component } from 'react';
import { withAuthConsumer } from '../../contexts/AuthStore';
import { Modal, Button } from 'react-bootstrap';
import calendarService from '../../services/calendar-service';

const validations = { title: (value) => value.length > 0 && value.length < 30}
class CreateEvent extends Component {

  state = {
    event: {
      title: '',
      classroom: this.props.classroom.id,
      start: '',
      end: '',
      role: ''
    },
    errors: {
      title: true
    },
    touch: {},
    show: false
  }

  handleChange = (event) => {

    const { name, value } = event.target;

    this.setState({ 
      event: {
        ...this.state.event,
        [name]: value
      },
      errors: {
        ...this.state.errors,
        [name]: validations[name] && validations[name](value)
      }
    })
  }

  handleBLur = (event) => {
    const { name } = event.target;

    this.setState({
      ...this.state.touch,
      [name]: true
    })
  }

  createEvent = (event) => {
    event.preventDefault();

    const eventData = {
      ...this.state.column
    }
    calendarService.createEvent(eventData)
    .then(this.props.fetchEvents)
    .then(this.setState({ show: false }))
  }

  handleClose = () => {this.setState({ show: false });}

  handleShow = () => {this.setState({ show: true });}

  render() {
    const { event, touch, errors, show } = this.state;


    return(
      <>
          <Button variant="outline-info" onClick={this.handleShow}>+ Create Event</Button>

          <Modal show={show} onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>New event</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <form onSubmit={this.handleSubmit}>
              <div className="form-group">
                <label htmlFor="titleEvent">Title</label>
                <input type="text" 
                  className={`form-control ${touch.email && errors.email && 'is-invalid'}`} 
                  id="titleEvent"
                  name="title"
                  onChange={this.handleChange}
                  onBlur={this.handleBlur}
                  value={event.title}
                  />
                  <div className="invalid-feedback">{errors.title}</div>
              </div>

              <div className="form-check form-check-inline">
                <input className={`form-check-input ${touch.role && errors.role && 'is-invalid'}`} 
                type="radio" 
                name="role" 
                id="roleRegister" 
                value="teacher"
                checked={event.role === "teacher"}
                onChange={this.handleChange}
                />
                <label className="form-check-label" htmlFor="roleRegister">Teacher</label>
              </div>
              <div className="form-check form-check-inline">
                <input className={`form-check-input ${touch.role && errors.role && 'custom-control-input'}`} 
                  type="radio" 
                  name="role" 
                  id="roleRegister2" 
                  value="student"
                  checked={user.role === "student"}
                  onChange={this.handleChange}
                  />
                <label className="form-check-label" htmlFor="roleRegister2">Student</label>
                <div className="invalid-feedback">{errors.role}</div>
              </div>

                <button type="submit" className="btn btn-info w-100 mt-3">Register</button>
            </form>
            </Modal.Body>
              <Modal.Footer>
                <Button variant="info" onClick={this.createEvent}>Create</Button>
              </Modal.Footer>
          </Modal>
      </>
    )
  }
}

export default withAuthConsumer(CreateEvent) ;
