import React, { Component } from 'react';
import { Button, Modal, Form } from 'react-bootstrap';


class CheckList extends Component {
    state = {
        show: false,
        date: ''
    }

    handleClose = () => {
        this.setState({ show: false });
      }

    handleShow = () => {
        this.setState({ show: true });
    }

    componentDidMount(){
      this.handleDate();
    }

    handleDate = () => {
      const date = new Date().toDateString();
      this.setState({ date });
    }

    render(props) {
        return (
            <>
                <Button variant="outline-secondary rounded-pill"  onClick={this.handleShow}>
                    Check list
                </Button>

                <Modal show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title className="d-flex flex-column">Check list today 
                        <small className="text-secondary">{this.state.date}</small>
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                      <div className="list-info">
                        <p className="text-info">Students in classroom</p>
                        <h1 className="list-total">{this.props.totalStudent}</h1>
                        <h1 className="list-num">{this.props.studentSelected.length}</h1>   
                      </div>
                      <div className="form-group">
                        <Form>
                        <label htmlFor="exampleFormControlTextarea1">Observations</label>
                        <textarea className="form-control" id="exampleFormControlTextarea1" rows="1"></textarea>
                        </Form>

                      </div>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                    </Button>
                        <Button variant="primary" onClick={this.handleClose}>
                            Save Changes
                    </Button>
                    </Modal.Footer>
                </Modal>
            </>
        )
    }


}

export default CheckList;
